// --- -10 by coyote

#define S    smoothstep( 12./R.y, 0., abs(length(U)-.9)
#define B    O += S-.05) - O* S-.07); U += D

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, D = vec2(.5,-.5),
         U = fract( ( 4.*u - R-R ) / R.y ) - .5; // tile = 1/4 circle
    O-=O; // -.5;                                //  3-grey variant
    U = abs( U.x*U.y<0. ? U.yx : U )             // tile = 1/16 circle
    - D; B
    +.5; B
    -.5; B;  
}






/** // --- 247 chars

#define S    smoothstep( 12./R.y, 0., abs(length(U)-.9)
#define B    O += S-.05) - O* S-.07)

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, D = vec2(.5,-.5),
         U = fract( ( 4.*u - R-R ) / R.y ) - .5; // tile = 1/4 circle
    O-=O; // -.5;                                //  3-grey variant
    U *= sign(U.x); 
    U = U.y<0. ? vec2(-U.y,U) : U;               // tile = 1/16 circle
    U -= D;    B;
    U += D+.5; B;
    U += D-.5; B;  
}






/** // --- 260 chars

#define S(U,r) smoothstep( 12./R.y, 0., abs(length(U)-.9) -r )
#define B(U)   O *= 1.-S(U,.07), O += S(U,.05)

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, D = vec2(.5,-.5),
         U = fract( ( 4.*u - R-R ) / R.y ) - .5; // tile = 1/4 circle
    O-=O; // -.5;                                //  3-grey variant
    U = U.x<0. ? -U : U;
    U = U.y<0. ? vec2(-U.y,U) : U;               // tile = 1/16 circle
    B(U-D);
    B(U+.5);
    B(U+D);  
}

