void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0, uv);
}

float cell(vec2 uv)
{
    return texture(iChannel0, uv/iResolution.xy).x;
}
float convolve(vec2 uv)
{
    return cell(uv+vec2(-1, 1))+cell(uv+vec2(0, 1))+cell(uv+vec2(1, 1))
          +cell(uv+vec2(-1, 0))+0.5*cell(uv)       +cell(uv+vec2(1, 0))
          +cell(uv+vec2(-1,-1))+cell(uv+vec2(0,-1))+cell(uv+vec2(1,-1));
}
float activation(float x)
{
    return float(abs(x-3.)<=.5);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float c = activation(convolve(fragCoord));
    if (iFrame == 0) {
        c = texture(iChannel1, fragCoord/iChannelResolution[1].xy).r;
    }
    fragColor = vec4(c,c,c,1.0);
}
