// Fork of "deep 9input" by lomateron. https://shadertoy.com/view/Nltyzs
// 2022-08-13 06:07:05

#define A(u) texture(iChannel0,(u)/iResolution.xy).x

int rule[10] = int[] (1,1,0,1,1,1,1,0,0,0);

void mainImage( out vec4 fragColor, in vec2 u )
{
  float a = +A(u+vec2( 1, 0))
           +A(u+vec2( 0, 1))
           +A(u+vec2(-1, 0))
           +A(u+vec2( 0,-1))
           +A(u+vec2( 1, 1))
           +A(u+vec2(-1, 1))
           +A(u+vec2( 1,-1))
           +A(u+vec2(-1,-1))
           +A(u+vec2( 0, 0));
  float o = float(rule[int(a)]);
  if(iFrame == 0 || iMouse.z > .5)
  {
    vec2 v = 1.*(u        *2.-iResolution.xy)/iResolution.y;
    vec2 m = 1.*(iMouse.xy*2.-iResolution.xy)/iResolution.y;
//  o = floor(fract(cos(dot(u,vec2(234.76543,iTime+22.5363)))*2467.5678)+.98);
    o *= step(dot(v,v),dot(m,m)*.5);
  }
  fragColor = vec4(o,a,a,a);
}
