#define ITER 256
#define SIZE 256.

void mainImage( out vec4 FCOL, in vec2 FPT ){

    ivec2 PT = ivec2(((FPT+FPT-iResolution.xy)/min(iResolution.x, iResolution.y))*SIZE*5.);
    ivec2 look = ivec2(sin(iTime)*SIZE, sin(iTime/3.)*float(SIZE)*2.);
    int scroll = int(iTime*84.);

    ivec2 sum = ivec2(0);
    ivec2 ray;
    int z;
    for(z = 0; z < ITER; ++z){
        sum = sum+PT+look;
        ray = sum>>8;
        ray.x += scroll;
        ray.y += scroll>>1;

        if((ray.x|ray.y^(z+scroll))%325 < 17)
            break;
    }

    ivec3 COL = ivec3(255);
    COL = ivec3(ITER-z);
    COL.yz += z*7>>3;

    FCOL = vec4(vec3(COL)/float(ITER), 1.);
}
