// coordinate tests.
// making a heart from angle and radius.
// .dean alex.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // center
    vec2 uv = fragCoord/iResolution.yy;
    uv.x -= iResolution.x/iResolution.y * 0.22;

    // polar coordinates
    float dx = uv.x - 0.5;
    float dy = uv.y - 0.5;
    float r = sqrt(dx*dx + dy*dy);
    float ang = atan(dx,dy) + iTime*0.1;

    // angle as scalar
    float a = fract(ang/6.283185307179586 + 1.0);

    // mirrored angle as scalar
    float am = 1.0 - abs(0.5 - a)/0.5;

    // mix radii to get heart
    float r1 = mix(0.8,1.04, am );
    float r2 = sin((am-1.0)*(am-1.0)*3.141592653589793238);
    float rheart = (r2*0.48 + pow(r1,4.0) - sin(am*2.0)*0.36) * 0.58;

    // shapes
    float insideHeart = 1.0 - step(rheart,r);
    float circle = step(0.5,r) * 0.5;
//    float circleToHeart = smoothstep( 0.5, rheart,r);
    float circleToHeart = rheart / r;

    // color
    vec3 col = vec3( insideHeart, circle, step(0.5,mod(fract(circleToHeart * 1.0001)*5.0,1.0))*0.4);
    fragColor = vec4(col,1.0);
}
