float PI = 3.14159;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.y;
    vec2 uv2 = fract(uv * 5.) - vec2(0.5, 0.5);
    float r = 0.4;
    float y = 0.1;
    float yMax = 0.9;
    float m = 2. + 10. * pow(0.5 * (1. + sin(iTime * 0.1)), 3.);
    float m2 = 2. + 10. * pow(0.5 * (1. + sin(iTime * 0.15)), 3.);
    float f = pow(1. - length(uv2), 1. + 10. * 0.5 * (1. + sin(iTime * 1. + floor(uv.x * m + uv.y * m2) * PI / 4.)));
    f = pow(0.5 * (1. + sin(f * 10.)), 5.);

    vec3 col = vec3(f);

    fragColor = vec4(col,1.0);
}
