void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy, st2 = fragCoord.xy/iResolution.xy, st3 = fragCoord.xy/iResolution.xy, st4 = fragCoord.xy/iResolution.xy;
    float timeCoefficient = sin(iTime), circleRadius = abs(timeCoefficient), tmp = timeCoefficient * (0.5 - (circleRadius / 2.0));

    //SYMMETRICAL
    /*st += vec2(tmp, -tmp);
    st2 -= vec2(tmp, tmp);
    st3 += vec2(tmp, tmp);
    st4 -= vec2(tmp, -tmp);*/

    //ASYMMETRICAL
    st += vec2(tmp, -tmp);
    st2 += vec2(tmp, tmp);
    st3 -= vec2(tmp, tmp);
    st4 -= vec2(tmp, -tmp);

    fragColor = vec4(abs(cos(iTime * 0.4)), 
                        abs(sin(iTime * 0.1)), 
                        	distance(st, vec2(0.125, 0.875)) < (circleRadius / 2.0) || 
                        	distance(st2, vec2(0.875, 0.875)) < (circleRadius / 2.0) || 
                        	distance(st3, vec2(0.125, 0.125)) < (circleRadius / 2.0) || 
                        	distance(st4, vec2(0.875, 0.125)) < (circleRadius / 2.0) ? (abs(timeCoefficient) + 0.4) : 0.0, 
                        1.0);
}
