void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
		vec2 uv = ( fragCoord -.5* iResolution.xy ) / iResolution.y;

        vec2 balls[5];

        float d = 0.;

        for (int i = 0; i < balls.length(); i++) {
            float index = float(i);
            float x = sin(index + 1. * 0.2 + iTime * 0.8599 + index) * 0.9;
            float y = cos(index + 1. * 0.189 + iTime * 1.9878) * 0.5;
            balls[i] = vec2(x, y);
            d += 1. / length(uv - balls[i]);
        }

        d = smoothstep(0.78, 0.8, d * 0.07);

		fragColor = vec4( d );
}
