// hash12 from https://www.shadertoy.com/view/4djSRW
float h(vec2 p)
{
	vec3 a  = fract(vec3(p.xyx) * .1031);
    a += dot(a, a.yzx + 33.33);
    return fract((a.x + a.y) * a.z);
}

void mainImage( out vec4 O, in vec2 I )
{
    vec2 p = I / 40.;

    O = vec4(step(abs(abs(fract(dot(vec2((floor(h(ceil(p))*2.-.5)),1),p))-.5)-.38),.06));
}
