renderpass.code:
vec2 diamonds (vec2 st) {
    vec2 r = vec2(0.0);
    r.x = st.x;
    r.y = abs(0.5-fract(st.x))+st.y*sign(fract(st.y)-0.5);

    return r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/min(iResolution.x, iResolution.y);

    uv *= 5.;

    // Time varying pixel color
    vec3 col = vec3(0.);

    uv = diamonds(uv);
    vec2 pos = floor(uv);


    // honeycomb the 2D grid
    col.r = 0.8*mod(pos.x+pos.y,1.6+0.5*sin(0.2432*iTime));
    col.g = 0.2*mod(pos.y,2.5+0.7*sin(0.1124*iTime));
    col.b = 0.3333*mod(pos.x+pos.y,3.2+0.8*sin(0.0845*iTime));

    // Output to screen
    fragColor = vec4(col,1.0);
}
