// change MACRO_SQ [FUNC]

// sq_IDEA_1
// sq_IDEA_2
// sq_IDEA_3
// sq_IDEA_123
// sq_IDEA_CRAZY_1
// sq_IDEA_CRAZY_2
// sq_IDEA_CRAZY_12
// sq_IDEA_CRAZY_12_WEIRD
// sq_IDEA_CRAZY_12_WEIRD_ALT
// sq_IDEA_FINAL_1
// sq_IDEA_FINAL_2
// sq_IDEA_FINAL_3

#define MACRO_SQ sq_IDEA_123



float cool_sq(in vec3 param){
    return param.x * param.y / abs(param.x) * param.z;
}

float compound_sq(in float f)
{
return cool_sq(vec3(f, .61, 0.9))
+ cool_sq(vec3(f, 1.43, .17))
* cool_sq(vec3(f,.37, 1.61))
+ cool_sq(vec3(f, 1.43, .17))
/ cool_sq(vec3(f,.37, 1.61));
}
float sq_IDEA_1( in float f ){
    return f * 0.9;
}
float sq_IDEA_2( in float f ){
    return f * 1.1 / abs(f);
}
float sq_IDEA_3( in float f ){
    return compound_sq(f);
}
float sq_IDEA_123( in float f ){
    return sq_IDEA_1(f) * sq_IDEA_2(f)- sq_IDEA_3(f) + sq_IDEA_1(f);
}

float sq_IDEA_CRAZY_1( in float f ){
    return sq_IDEA_123(f) * sq_IDEA_123(f)- sq_IDEA_123(f) + sq_IDEA_123(f);
}
float sq_IDEA_CRAZY_2( in float f ){
    return sq_IDEA_123(f) - sq_IDEA_123(f)+ sq_IDEA_123(f) * sq_IDEA_123(f);
}

float sq_IDEA_CRAZY_12( in float f ){
    return sq_IDEA_CRAZY_1(f) * sq_IDEA_CRAZY_2(f);
}
float sq_IDEA_CRAZY_12_WEIRD( in float f ){
    return sq_IDEA_CRAZY_1(-f) * sq_IDEA_CRAZY_2(-f);
}
float sq_IDEA_CRAZY_12_WEIRD_ALT( in float f ){
    return sq_IDEA_CRAZY_1(-f*1.2) * sq_IDEA_CRAZY_2(f*11.);
}
float sq_IDEA_FINAL_1( in float f ){
    return sq_IDEA_CRAZY_2(f)  * sq_IDEA_CRAZY_12_WEIRD_ALT(f);
}
float sq_IDEA_FINAL_2( in float f ){
    return sq_IDEA_CRAZY_2(f) - sq_IDEA_CRAZY_1(f) * sq_IDEA_CRAZY_12_WEIRD_ALT(f);
}
float sq_IDEA_FINAL_3( in float f ){
    return sq_IDEA_FINAL_1(f) + sq_IDEA_FINAL_2(f - 0.3);
}

float sq( in float f ){
   return MACRO_SQ(f);   
}

vec3 sq3( in vec3 v ){
    return vec3(
        sq(v.x),
        sq(v.y),
        sq(v.z)
    );
}

vec3 sh( in vec3 pos, in vec2 uv )
{
    vec2 xy = (uv.xy - pos.xy) / pos.z;
    float len = dot(xy,xy);
    return vec3( xy, sq( 1.0 - len ));
}
float tsin( in float mf ){
    return sin( iTime * mf);
}
float tcos( in float mf ){
    return cos( iTime * mf);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec3 norm;
    float w = iResolution.x;
    float h = iResolution.y;
    float m = min(w,h);

    vec3 s1 = sh(
        vec3( w * 0.5, h * 0.5, m * 0.5 ),
        vec2( fragCoord.x, fragCoord.y )
    );
    vec3 s2 = sh(
        vec3( (tsin(1.7)*0.4 + 0.5)*w, (tcos(1.3)*0.4 + 0.5)*h, m * 0.1 ),
        vec2( fragCoord.x, fragCoord.y )
    );
    vec3 s3 = sh(
        vec3( (tsin(0.5)*0.3 + 0.5)*w, (tcos(-2.0)*0.4 + 0.5)*h, m * 0.5 ),
        vec2( fragCoord.x, fragCoord.y )
    );
    vec3 s4 = sh(
        vec3( (tcos(-0.31)*0.6 + 0.5)*w, (tsin(-0.4)*0.2 + 0.5)*h, m * 3.3 ),
        vec2( fragCoord.x, fragCoord.y )
    );

    //
    norm = sq3( cross( s2.yzx, s1.zxy ));
    norm = sq3( cross( norm, s3.zxy ));
    norm = sq3( cross( -norm, s4.xyz ));


    //

    float mag = length( norm );
    float a = pow( smoothstep( 1.0, 1.0, mag ), 10.5 );
    float s = atan( norm.z, norm.x ) / 6.283185307179586;
    float t = asin( norm.y ) / 3.14159265358979;
    fragColor = texture( iChannel0, vec2( s*5.1 + iTime*-0.2, t * s ));
    fragColor.b = min( fragColor.g, fragColor.b );
    vec3 lightDir = normalize( vec3( tcos(.5) * -1.1, 0.3, tsin(.5) *1.7 ));
    float mflight = max(dot( norm, lightDir), 0.0);
    float dis1 = 1.0 - length( norm - lightDir ) / 2.0;
    mflight += pow( dis1, 10.0) * 1.5;
    fragColor.rgb *= mflight;
    vec3 l1p = vec3( tsin(1.7) * 0.6, tcos(3.5) * 0.5, -tsin(1.5) * 0.3 );
    vec3 l1c = vec3( 3.0, 2.0, 1.0 );
    float dis = 1.0 - length( norm - l1p ) / 2.0;
    fragColor.rgb += l1c * pow( dis, 0.8 );
    fragColor = mix( fragColor, vec4(1.0,0.0,0.0,1.0), a );
}
