// http://www.pouet.net/prod.php?which=57245
// If you intend to reuse this shader, please add credits to 'Danilo Guanabara'

#define t iTime*1.0 //divide/multiply to speed up/slow down
#define r iResolution.xy

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec3 c;
	float l,z=t;

    int colors = 6; //edit this to the number of colors you want to
                    //be displayed

	for(int i=0;i<colors;i+=1) {
		vec2 uv,p=fragCoord.xy/r;
		uv=(p); // floor() or ceil() this to make it symmetrical in the four quadrants

		p-=(.5); // floor() this to shift the source in the bottom-left corner
                 // ceil() it to shift the source in the top-right corner
                 // or you can just set it to 0.0 or 1.0

		p.x*=r.x/r.y; // hehe if you change the "*" for "/" it gets wide

		z+=.07/1.;
		l=length(p)/1.0; // use this number to zoom in an add light
                         // (multiply to zoom out and less light)

		uv+=p/l*(sin(z)+1.)*abs(sin(l*9.-z-z)); // replace "abs()" with "floor" to make
                                                  // it look like a CD
		c[i]=.01/length(mod(uv,1.)-.5);
	}
	fragColor=vec4(c/l,t);
}
