void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = fragCoord.xy/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;

    float time = iTime*0.064;
    float otherTime = iTime*1.0+st.y;

	vec3 color = vec3(0.1, 0.0, 0.3);
    float a = abs(sin(((st.x+time)*10.0+(st.y-1.5)*(st.x-0.9)*10.0)*4.0));
    color += vec3(pow(a, 6.000), pow(a, 100.0)*abs(sin((time*10.0)+5.0)), pow(a, 3.696));

    fragColor = vec4(color,1.0);
}
