void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.0 * fragCoord/iResolution.xy - 1.0;
    uv.x *= iResolution.x/iResolution.y;
    vec3 st = texture(iChannel1, vec2(iTime)).xyz;

    float theta = atan(uv.x, uv.y);
    float r =  0.8 - (sin(5.0 * theta + iTime) + st.x) * 0.2;
    float d = length(uv) - r;

    vec3 col = vec3(0);
    if(d < 0.0){
        col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    }

    fragColor = vec4(col,1.0);
}
