#define S(a, b, t) smoothstep(a, b, t)

// Flag of the Bahamas
// Based on BigWIngs flag tutorial
// https://www.shadertoy.com/view/flsXRM

float PI = 3.14159;
vec3 BLUE = vec3(0, 119, 139)/255.; // PANTONE 3145 
vec3 YELLOW = vec3(255, 199, 44)/255.; // PANTONE 123
vec3 BLACK = vec3(0.1); // A little grey so we can see the shading

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    // Wavy uv
    float t = uv.x*2.-2.*iTime+uv.y*3.;
    uv.y += sin(t)*.05;

    // Default color
    vec3 col = BLACK;

    // Pixel width
    float w = fwidth(uv.x);

    // Draw stripes
    // Stripe width. Three equal stripes, so 1/3 is yellow.  
    float swidth = 1./3./2.;
    // AA between background and stripe
    float hstripe = S(swidth-w, swidth+w, abs(uv.y));
    // Color the stripes
    col = mix(YELLOW, BLUE, hstripe);

    // Triangle
    vec2 sv = uv+vec2(0.025, 0.);
    sv.y = abs(sv.y);
    float tri = dot(sv, vec2(tan(PI/6.), 1.));
    tri = S(-w, w, tri);
    col = mix(BLACK, col, tri);

    // Shading 
    col *= .7+cos(t)*.3;

    // Clip top and bottom
    col *= S(w, .0, abs(uv.y)-.5);

    // Output to screen
    fragColor = vec4(col, 1.0);
}
