void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 col = vec4(0.);

    ivec2 fragI = ivec2(fragCoord);

    vec4 coord = texelFetch(iChannel1, ivec2(0, 0), 0) * 4.;

    coord.z -= iResolution.y / 2.;

    if(iFrame != 0){
        col = texture(iChannel0, uv);


        vec2 dif = abs(fragCoord - coord.xy - (.5 * iResolution.xy));

        if(dif.x < .5 && dif.y < .5){
            col = vec4(cos(iTime), sin(iTime), cos(iTime + 1.5), 1.);
        }
    }

    fragColor = col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 fragI = ivec2(fragCoord);

    vec4 col = vec4(0.);

    vec4 coord = texelFetch(iChannel0, ivec2(0, 0), 0);

    if(fragI.x == 0 && fragI.y == 0){
        if(iFrame == 0){
            col = vec4(1.);
        }
        else{
            vec4 delta = vec4(sigma * (coord.y - coord.x), coord.x * (rho - coord.z), (coord.x * coord.y) - (coord.z * beta), 0.);

            col = coord + (delta * iTimeDelta);

        }
    }
    fragColor = col;
}
