void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // 得到 UV
    vec2 uv = fragCoord/iResolution.xy;
    // 偏移 x 使之处于屏幕中央且宽高相等
    uv.x = uv.x * iResolution.x / iResolution.y - 0.438f;

    // 外圆半径
    float radiusOutside = 0.5f;

    // 内圆半径
    float radiusInside = 0.4f;

    // 笔刷硬度
    float density = 100.f;

    // 外圆
    float circleOutside = 1.f - (distance(uv, vec2(0.5, 0.5)) + (1.f - clamp(radiusOutside, 0.f, 1.f)));
    // 内圆
    float circleInside = 1.f - (distance(uv, vec2(0.5, 0.5)) + (1.f - clamp(radiusInside, 0.f, 1.f)));

    circleOutside = clamp(circleOutside * density, 0.f, 1.f);
    circleInside = clamp(circleInside * density, 0.f, 1.f);

    // 外圆 - 内圆
    float circle = circleOutside - circleInside;

    fragColor = vec4(circle);
}
