mat2 r2d(float a) { float c= cos(a), s = sin(a); return mat2(c, -s, s, c); }
#define sat(a) clamp(a, 0., 1.)
#define PI 3.14159265
vec3 rdr(vec2 uv)
{
    vec3 col = vec3(0., 0., 0.);
    float t = iTime*.5;
    float globalRotateTime = 1.;
    uv *= r2d(PI/2.*floor(t/globalRotateTime));

    vec2 rep = vec2(0.1);
    vec2 id = floor((uv+rep*.5)/rep);


    float beat = 2.*rep.y*2.;

    float vtime = t*sat(sign(sin(t*2.*PI/beat)));//mod(t, rep.y);
    uv.y += mod(id.x,2.)*vtime;//vtime;
    uv = mod(uv+rep*.5,rep)-rep*.5;
    float shape = length(uv)-.01;

    col = mix(col, vec3(1., 1., 1.), 1.-sat(shape*iResolution.x*.5));

    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.xx;

    vec3 col = rdr(uv);

    fragColor = vec4(col, 1.);
}
