vec3 rgb(float r, float g, float b) {
	return vec3(r / 255.0, g / 255.0, b / 255.0);
}

vec4 rectangle(vec2 uv, vec2 pos, float width, float height) {
	float t = 0.0;
	if ((uv.x > pos.x - width / 2.0) && (uv.x < pos.x + width / 2.0)
		&& (uv.y > pos.y - height / 2.0) && (uv.y < pos.y + height / 2.0)) {
            t = 1.0;
    }
    return vec4(t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy;
    vec2 center = iResolution.xy * 0.5;
    float width = iResolution.x * 0.25;
    float height = iResolution.x * 0.25;
    vec4 tintColor = vec4(rgb(100.0, 0.0, 0.0), 0.0);
    vec4 layer2 = mix(texture(iChannel0, fragCoord/iResolution.xy), texture(iChannel0, fragCoord/iResolution.xy) + tintColor,
                        rectangle(uv, center, width, height));

    uv = fragCoord/iResolution.xy;
    vec4 layer1 = texture(iChannel0, uv);
    fragColor = mix(layer1, layer2, layer2.a);
}
