#define PI 3.141592
#define PI_2 6.283185

vec4 Color1 = vec4(0.0, 0.0, 0.0, 1.0);
vec4 Color2 = vec4(1.0, 1.0, 1.0, 1.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
	vec2 uv = fragCoord/iResolution.xy;

                                            // In degrees, because this is designed for humans
    float startAngle = -53.5 * PI / 180.0;  // Angle for the black end, relative to straight left
    float sweepArc = 107.0 * PI / 180.0;    // Angle range over which to go from black to white

 	float pixelAngle = atan(uv.y - 0.5, uv.x - 0.5);
    float value = (pixelAngle - startAngle) / sweepArc;

    vec4 color = mix(Color1, Color2, clamp(value, 0.0, 1.0));
	fragColor = color;
}
