/*
Both the standard Mandelbrot set and julia sets can be calculated using 
pixel position as initial Z.. The only difference then is whether C 
is a constant across the whole image, or varied based on pixel position.
This shader shows what happens when you let C be influenced only partially by pixel position. 

It animates an interpolation for C between a moving constant value for
animating Julia fractals, and pixel position.
*/


#define PI 3.14159
#define TAU 6.28318
#define maxIterations 100
#define AA 2


vec2 cMult(vec2 c1, vec2 c2){
   //complex mult
    float newR = c1.x*c2.x - c1.y*c2.y;
	float newI = c1.y*c2.x + c1.x*c2.y;
	return vec2(newR,newI);
}
vec2 cDivide(vec2 c1, vec2 c2){
	//conjugate = a - bi;
	//to divide, multiply both sides by complex conjugate of denom

	float divisor = dot(c2,c2);

	return vec2((c1.x*c2.x + c1.y*c2.y)/divisor, (c1.y*c2.x - c1.x*c2.y)/divisor);
}
vec2 cPow(vec2 z, float p){
    if (p==1.) return z;
	float radius = sqrt(z.x*z.x + z.y*z.y);
	float theta = atan(z.y,z.x);
	float newR = pow(radius, p);
	return vec2(newR * cos(theta*p), newR * sin(theta*p));
}
vec2 cPow(vec2 c1, vec2 c2){
    return vec2(0);
}

vec2 f1(vec2 z, vec2 c) {
    float t = iTime/2.;
    vec2 jul = vec2(sin(t)*0.75-0.5,cos(t)*0.5);
    float conv = cos(TAU*iTime/60.)/2.+0.5;
    return cMult(z,z) + jul*(1.-conv) + c*conv;///mandelbrot

}

vec3 palette(float loc, vec3 a, vec3 b, vec3 c, vec3 d) {
    return a + b*cos( TAU*(c*loc+d) );
}

float logPotential(float d,float i){

	float base=log(2.);
	return i-(log(log(d)/base)/base);

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec3 aacol=vec3(0.);
    float time = iTime;

    //aa code here
    for (int aax=0; aax<AA; aax++){
    for (int aay=0; aay<AA; aay++){

        vec2 z = vec2(0.);

        vec2 uv = (fragCoord + vec2(aax,aay)/float(AA))/iResolution.xx;
        uv -= 0.5;uv *= 1.3;uv += 0.5;
        float zoom = 3.;


        vec2 c = vec2(zoom * (uv.x - 0.5), zoom * (uv.y - 0.22));
        z = c;
        if(iMouse.z>0.){
            vec2 mPos = iMouse.xy/iResolution.xx;
            mPos -= 0.5; mPos *= 1.3; mPos += 0.5;
            mPos=zoom*(mPos-vec2(0.5,0.22));
            c=mPos;
        }

        bool escaped = false;

        int i = 0;


        //iterate

        for (i = 0; i < maxIterations; i++) {
          z = f1(z,c);
          if (length(z)>50.) {
              escaped=true;
              break;

          }
        }




    vec3 iterationCol = vec3(palette(float(i)/20., vec3(0.5),vec3(0.5),vec3(1.0, 1.0, 0.0),vec3(0.3, 0.2, 0.2)));

    if (!escaped)iterationCol=iterationCol*0.8 -0.3;
    aacol+= iterationCol;
    }
    }
    fragColor=vec4(aacol.xyz/4.,1.);
}
