renderpass.code:
vec4 rgba(float r, float g, float b, float a)
{
    return vec4(r / 255.0, g / 255.0, b / 255.0, a / 255.0);
}

float sdCircle(vec2 p, float r)
{
    return length(p) - r;
}

float sdRect(vec2 p, vec2 s)
{
    vec2 c = abs(p) - s;
    float o = length(max(c, vec2(0)));
    float i = min(max(c.x, c.y), 0.);
    return o + i;
}

float opSolid(float d)
{
    float dc = fwidth(d) * 0.7;

    return smoothstep(dc, -dc, d);
}

float opAdd(float d1, float d2)
{
    return min(d1, d2);
}

float opSubtract(float d1, float d2)
{
    return max(d1, -d2);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy - 0.5f;
    uv.x *= iResolution.x / iResolution.y;

    float s = 0.4;
    float i = 0.1;

    float d = sdCircle(uv, s);
    float d2 = sdCircle(uv, s - i);
    float d3 = sdRect(uv, vec2(s-(i/2.), i/2.));

    d = opSubtract(d, d2);
    d = opAdd(d, d3);
    d = opSolid(d);

    fragColor = mix(rgba(201., 201.,210., 255.), rgba(255., 56., 0., 35.), d);
}
