void mainImage(out vec4 O, vec2 I)
{
    vec3 r = iResolution,
    T = iTime + vec3(0,2,1),
    P = (I+I-r.xy)/r.y*mat3x2(7,4, 0,-8, -7,4) / 5.+.6;

    int A = int(T.z)/2%3;
    O = (.5-length(fract(
    P += (T-sin(T*6.283)/6.).x*floor(mod(T,3.)-1.)*cos(ceil(P[int(T.y)%3])*3.14))
    -.5))*.2*r.y*mod(ceil(P+vec3(A<1,A<2,A-1)),2.).xyzz;
}
