void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = texture(iChannel0, uv).rgb;
    fragColor = vec4(col,1.0);
}

float circle (vec2 uv, float iTime) {
    float c = 1. - length(uv + vec2(sin(iTime * 1.3) * .2, cos(iTime * 2.3) * .02));
    c = smoothstep(0.97, 0.971, c);
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.x;

    float c = 0.;

    for (int i = 0; i < 5; i++) {
        c += circle(uv + vec2(0., -0.2 + 0.1 * float(i)), iTime + float(i) * 0.2);
    }


    vec3 col = vec3(c);
    fragColor = vec4(col,1.0);
}

