void mainImage( out vec4 O, in vec2 I ) {
    vec2 u = I / iResolution.xy;
    vec2 st = u - .5;
    st *= 1.009;
    st += .5;
    vec3 t1 = texture(iChannel0, u).rgb;
    vec3 t2 = texture(iChannel1, st).rgb;
    O = vec4((t1 + t2 * .96).r, (t1 + t2 * .94).g, (t1 + t2 * .9).b, 1.);
}
