#ifdef GL_ES
precision mediump float;
#endif

const float Speed = 8.0;

//Reference: https://thebookofshaders.com/10/
float random(vec2 st)
{
    return fract(sin(dot(st.xy, vec2(12.9898,78.233))) * 43758.5453123);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
  vec2 uv = (fragCoord.xy * 40. - iResolution.xy * 0.5) / min(iResolution.x, iResolution.y);
  vec3 col;
  vec3 color = vec3(1., 1., random(uv));
  for(float j = 0.0; j < 5.; j++)
      for(float i = 1.; i < Speed; i++)
      {
          uv.x += uv.y * sin(iTime) / cos(iResolution.x);
          col[int(j)] = clamp((abs(uv.x+uv.y)), 0.1, 1.0);
      }

  vec3 bg = vec3(random(uv), random(uv), random(uv));
  color = mix(col, bg, abs(sin(iTime)));
  fragColor = vec4(vec3(color), 1.);
}
