mat2 rotationMatrix(float angle)
{
	angle *= 3.14159 / 180.0;
    float s=sin(angle), c=cos(angle);
    return mat2( c, -s, s, c );
}

float sdfBox(vec2 p, vec2 size)
{
    vec2 d = abs(p) - size;  
	return length(max(d, vec2(0))) + min(max(d.x, d.y), 0.0);
}

float sdCircle( vec2 uv, float r )
{
    return length(uv) - r;
}

float one( vec2 uv)
{
    float f = sdfBox(uv + vec2(0.63,0), vec2(0.06, 0.18) );
    float f1 = sdfBox(uv + vec2(0.68,-0.13), vec2(0.05, 0.05) );
    return min(f,f1);
}

float zero( vec2 uv){
    float d = sdCircle(uv / vec2(.96,1.0) + vec2(0.33,0.07), .20);
    float d2 = sdCircle(uv / vec2(.80,1.0) + vec2(0.395,0.07), .10);
    return -min(-d,d2);
}

float zpercent( vec2 uv){
    float d = sdCircle( uv * vec2(1.3,1.) + vec2(0.45,-0.01), .095);
    float d2 = sdCircle( uv * vec2(1.65,1.) + vec2(0.57,-0.01), .045);
    return -min(-d,d2);
}

float barpercent( vec2 uv){
    vec2 rotuv = uv*rotationMatrix( -50.0 );
    return sdfBox( rotuv - vec2( 0.29, -0.475), vec2(.19,.03) ) ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy) / min(iResolution.x, iResolution.y);
    uv-= vec2(0.0,0.05); 

    float x = min( zero(uv), one(uv + vec2(0.,0.06) ) );
    x = min( x, zero(uv + vec2(-0.435,0.)) );
    x = min( x, zpercent(uv + vec2(-.8,0.)) );
    x = min( x, zpercent(uv + vec2(-1.006,0.175)) );
    x = min( x, barpercent(uv) );
    float textmask = sign( x );


    float stripemask = 1.0-smoothstep(0.,.25,cos(75.0*x - iTime*3.5) ) ;
    vec3 stripecol = mix( vec3(104,226,113), vec3( 77,182,90), uv.y+.5 )/255.;

    vec3 bgcol = mix( vec3(20,190,199), vec3(0,251,251), uv.y+.5 )/255.;
    bgcol = mix( bgcol, stripecol, stripemask);

    vec3 col = mix( bgcol, vec3(1.), 1.-textmask);
    col = mix( col, vec3(1.0), 1.0-smoothstep(0.,.004,abs(x)) );
    col = clamp( col, 0., 1.)* 1.-dot(uv,uv)*0.25;
    fragColor = vec4(col,1.0);
}
