mat2 rotate (float a)
{
    return mat2(cos(a), -sin(a),
                sin(a), cos(a));
}

float random(vec2 uv)
{
    return fract(sin(dot(uv*3.10, vec2(83.32,-373.9))*8.102)*39.938);
}

float noise (vec2 uv)
{
    vec2 i = floor(uv);
    vec2 f = fract(uv);

    float a = random(i);
    float b = random(i+vec2(1.,0.));
    float c = random(i+vec2(0.,1.));
    float d = random(i+vec2(1.));

    vec2 u = f*f*(3.-2.*f);

    return mix(a, b, u.x) +
    (c-a)*u.y*(1.0-u.x)+
    (d-b)*u.x*u.y;
}

float wood (vec2 uv, float p, float f)
{
    uv *= p;
    uv *= rotate(noise(uv*0.4)*0.2+iTime*0.1);

    uv = mod(uv, f);

    return smoothstep(0., 0.2, uv.y) -smoothstep(0.8,1., uv.y);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.xy;
    uv *= vec2(2.,5.);

    uv += vec2(cos(iTime*0.5), sin(iTime*0.5))*2.;

    float pct = wood(uv, 20., 4.);

    // Time varying pixel color
    vec3 col = 0.3*(noise(uv+iTime)+1.)+(vec3(0.0,0.1,0.5));//noise(uv);//0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    col += vec3(pct*0.3);

    // Output to screen
    fragColor = vec4(col,1.0);
}
