void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float pi = 3.14;


    float x= uv.x *iMouse.x/1024.0;
    float y= uv.y *iMouse.y/576.0;

    float circy = x*(3.0*y-sin(470.0) -1.5);
    float circx = (5.0*x-tan(470.0/pi)*pi/2.0-2.5);

    float f = circy*circy + circx*circx;



    vec3 col = vec3( 0.85, (3.0-3.0*f), 0.5);

    fragColor = vec4(col, 1.0);


