// Globals
float aspectRatio;
vec4 sphere = vec4(0.0,1.0,0.0,1.0); //xyzw
vec3 light = normalize(vec3(-1.0, -0.8, -1));
float ambientLight = 0.15;
float softnessFactor = 32.0;

vec3 sphereNormal(in vec4 sphere, in vec3 position) {
    // Sphere normals are just directed radially (normalize by radius)
    return (position - sphere.xyz) / sphere.w;
}

vec3 planeNormal() {
    return vec3(0.0,1.0,0.0);
}

float sphereIntersect(in vec3 rayOrigin, in vec3 rayDirection, in vec4 sphere) {

    // Equation of sphere: |(x,y,z) - center|^2 = r^2
    // Equation of ray: ray_O + ray_D*t = (x,y,z)
    // Solving for t, the length of the ray that intersects the sphere:
    // |ray_O - center|^2 + 2*<ray_D, ray_O - center>t + t^2 - r^2 = 0 (ray_D is unit length)
    // This is a quadratic equation which we can solve for t.

    float r = sphere.w; 
    vec3 center = sphere.xyz;
    vec3 rayOriginOffset = rayOrigin - center;

    float b = 2.0*dot(rayDirection, rayOriginOffset);
    float c = dot(rayOriginOffset, rayOriginOffset) - r*r;
    float discriminant = b*b - 4.0*c;

    if (discriminant < 0.0) return -1.0; // no intersection

    return ((-b - sqrt(discriminant)) / 2.0); // return smaller intersection value

}

float planeIntersect(in vec3 rayOrigin, in vec3 rayDirection) {
    // Equation of plane: y = 0 (horizontal plane into the screen)
    // Equation of ray: ray_O + ray_D*t = (x,y,z)
    // Isolating the y component: ray_O_y + ray_D_y*t = 0
    // Thus we can easily solve for t, where the plane and ray intersect.

    return -(rayOrigin.y / rayDirection.y);
}

// Cast ray at shapes in the scene and see what it intersects
// Returns ID of object it hit, if any. Also returns intersection distance as out param.
int castRay(in vec3 rayOrigin, in vec3 rayDirection, out float dist) {
    int ID = -1;
    dist = 1000.0f; // essentially functions as far-clip distance 

    float tSphere = sphereIntersect(rayOrigin, rayDirection, sphere);
    float tPlane = planeIntersect(rayOrigin, rayDirection);

    if (tSphere > 0.0) {
        dist = tSphere;
        ID = 1;
    }
    if (tPlane > 0.0 && tPlane < dist) {
        dist = tPlane;
        ID = 2;
    }

    return ID;
}

vec3 rayPosition(in vec3 rayOrigin, in vec3 rayDirection, in float t) {
    return (rayOrigin + (rayDirection * t));
}

vec2 scaleUV(in vec2 uv) {
    return (( -1.0 + 2.0 * uv) * vec2(aspectRatio, 1.0));
}

void animateSphere() {
    sphere.x = 0.5*cos(iTime);
    sphere.z = 0.5*sin(iTime);
}

// Calculates factor to scale color by depending on if a point is in shadow
float calcShadow(in vec3 rayOrigin) {
    float t;
    int ID = castRay(rayOrigin, -light, t);
    if (ID != -1) return 0.0; // intersected something, thus in the shadow.
    // intersected nothing, thus in light;
    return (1.0);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    animateSphere();

    // Normalized pixel coordinates (from 0 to 1)
    aspectRatio = iResolution.x/iResolution.y;
    vec2 uv = (fragCoord / iResolution.xy);
    vec2 scaledUV = scaleUV(uv); // shift domain to -1:1, then scale to aspect ratio

    // Create a ray with given origin and direction pointing at fragment
    vec3 rayOrigin = vec3(0.0, 0.8, 3.0);
    vec3 rayDirection = normalize(vec3(scaledUV, -1.0)); 

    // Cast ray and find point of intersection
    float distIntersect;
    int intersectionID = castRay(rayOrigin, rayDirection, distIntersect);
    vec3 position = rayPosition(rayOrigin, rayDirection, distIntersect);

    // Draw black by default
    vec3 col = vec3(0.0, 0.0, 0.0);
    if (intersectionID == 1) {
        vec3 normal = sphereNormal(sphere, position);
        float brightness = -dot(normal, light);
        col = vec3(0.3, 0.6, 0.7) * brightness + ambientLight;
    }
    else if (intersectionID == 2) {
        vec3 posForShadowCalc = vec3(position.x, position.y + 0.001, position.z);
        float shadow = calcShadow(posForShadowCalc);
        col = vec3(shadow * 0.55) + ambientLight;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
