float stroke(float x, float w, float s){
    float d = step(s, x+w*.5) - step(s,x-w*.5);
    return clamp(d,0.,1.);
}
float circleSDF(vec2 st){
    return length(st-.5)*2.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 st = fragCoord/iResolution.xy;
    st.x*= iResolution.x/iResolution.y;
    st.x-=0.5;
    st.x += sin(st.y*4.0+iTime)*0.1;
    //vec3 col = vec3(0.);
    vec3 col = texture(iChannel1,uv).xyz;
    vec3 strok = vec3(0.0);
    for (float i=-0.8; i<=0.9;i+=0.1){
        strok += stroke(circleSDF(st+i),0.05,0.8);
        strok += stroke(circleSDF((st+i)*st.y),0.05,0.8);
        strok += stroke(circleSDF((st+i)*st.x),0.05,0.8);
        strok += stroke(circleSDF((st+i)*-st.y),0.05,0.8);
        strok += stroke(circleSDF((st+i)*-st.x),0.05,0.8);
    }
    vec3 strokI = vec3(1.-strok);
    vec3 col2 = vec3(mix(col,strokI,strokI));
    //vec3 col3 = 1.-col;
    //col = mix(col3,col2,1.-strokI);
    fragColor = vec4(col2,1.0);
}

vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d){
    return a+b*cos(6.28318*(c*t+d));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x*= iResolution.x/iResolution.y;
    uv -= 0.5;
    uv.y+=0.5;
    uv.x+=0.1;
    uv.x+= sin(uv.y*25.)*0.3;
    uv.y+= sin(uv.x*8.0)*0.1;
    float saut = sin(iTime*3.14*0.5)*0.1225;
    uv.y -=saut;
    uv.x +=saut;
    //uv.y+= sin(uv.x*.05*iTime)*0.3;
    // Time varying pixel color
    vec3 col = vec3(0.);
    //col += vec3(uv.y+0.1,uv.x-0.1,uv.x-0.5);
    //col += vec3(uv,0.0);
    col += palette( uv.x, vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(1.0,1.0,0.5),vec3(0.8,0.90,0.30) );

    col += texture(iChannel0,uv).xyz;
    uv.x+=sin(uv.x*iTime*0.5)+0.5;
    col*=vec3(0.65);

    // Output to screen
    fragColor = vec4(col,1.0);
}
