float sdfSphere(vec3 uv, vec3 c, float r) {
    return pow(max(r - length(uv - c), 0.), 0.3);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    float col = 0.;

    float c1 = sdfSphere(vec3(uv,0.), vec3(0.), .2);
    float c2 = sdfSphere(vec3(uv,0.), vec3(vec2(-.15,.15),0.), .1);
    float c3 = sdfSphere(vec3(uv,0.), vec3(vec2(.15,.15),0.), .1);

    col = max(col,c1);
    col = max(col,c2);
    col = max(col,c3);

    float e1 = sdfSphere(vec3(uv,0.), vec3(vec2(-.08,.05),0.), .03);
    float e2 = sdfSphere(vec3(uv,0.), vec3(vec2(.08,.05),0.), .03);
    col -= e1;
    col -= e2;

    float b1 = sdfSphere(vec3(uv,0.), vec3(vec2(-.08,.05),0.), .023);
    float b2 = sdfSphere(vec3(uv,0.), vec3(vec2(.08,.05),0.), .023);
    col += b1;
    col += b2;

    float m1 = sdfSphere(vec3(uv,0.), vec3(vec2(0.,-.04),0.), .049);
    float m2 = sdfSphere(vec3(uv,0.), vec3(vec2(0.,-.07),0.), .035);    
    col -= min(m1,m2);

    fragColor = vec4(vec3(col),1.0);
}
