    float Crcl(vec2 xy, float r, vec2 pos)
    {
        float d = length(xy-pos);
        float cl = smoothstep(r, r-0.008, d);
        return cl;
    }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy/iResolution.xy;
    xy -= 0.5;
    xy.x *= iResolution.x/iResolution.y;

    float smesh = Crcl(xy, 0.2, vec2(0.0, -0.1));
        smesh += Crcl(xy, 0.065, vec2(-0.12, -0.3));
        smesh += Crcl(xy, 0.065, vec2(0.12, -0.3));
        smesh += Crcl(xy, 0.07, vec2(-0.22, -0.02));
        smesh += Crcl(xy, 0.07, vec2(0.22, -0.02));
        smesh -= Crcl(xy, 0.03, vec2(-0.05, -0.09));
        smesh -= Crcl(xy, 0.03, vec2(0.05, -0.09));
        smesh -= Crcl(xy, 0.04, vec2(0.0, -0.14));
        smesh += Crcl(xy, 0.04, vec2(0.0, -0.13));

        smesh += Crcl(xy, 0.07, vec2(-0.18, 0.035));
        smesh += Crcl(xy, 0.07, vec2(0.18, 0.035));
        smesh += Crcl(xy, 0.07, vec2(-0.25, 0.035));
        smesh += Crcl(xy, 0.07, vec2(0.25, 0.035));

        smesh += Crcl(xy, 0.07, vec2(0.0, 0.155));
        smesh += Crcl(xy, 0.07, vec2(0.03, 0.105));
        smesh += Crcl(xy, 0.07, vec2(-0.03, 0.105));

        smesh += Crcl(xy, 0.045, vec2(0.22, -0.15));
        smesh += Crcl(xy, 0.045, vec2(-0.22, -0.15));

    vec3 pct = vec3(1.0, 1.0, 1.0) * smesh;
    fragColor = vec4(pct, 0.0);
}
