    rp/=vec3(0.2,0.2,0.3);

    float lE=(opExtrussion(rp,letraE(rp.xy),0.06)*0.3)-d;
    float chip1=max(chip,-rp.x);
    float chip2=max(chip,rp.x);


    float e1=max(lE,-rp.x);
    float e2=max(lE,rp.x);

    dis = comp(dis,vec2(chip1,1.0));

    dis = comp(dis,vec2(chip2,1.1));



    dis = comp(dis,vec2(e1,1.1));
    dis = comp(dis,vec2(e2,1.0));









    return dis;
}




vec4 castRay(vec3 ro , vec3 rd,float imax){

    float i = 0.002;
    vec2 dat;

    for(int n = 0; n < 256; n++){

        dat.xy = map(ro+rd*i);

        if(dat.x < 0.002)return vec4(ro+rd*i,dat.y);

        if(i > imax)return vec4(ro+rd*i,-1.1);

        i+=max(abs(dat.x),0.002);
    }

    return vec4(ro+rd*i,-1.1);
}


