float F(float x, float y) {
    return x * x + y * y - 1.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalized pixel coordinates (from -1 to 1)
    vec2 p = 1.0 - 2.0 * fragCoord / iResolution.xy;
    vec2 m = vec2(0.2, 0.4);

    float x = p.x * iResolution.x / iResolution.y;
    float y = p.y;

    float mx = m.x * iResolution.x / iResolution.y;
    float my = m.y;

    x /= mx;
    y /= my;

    float z = F(x, y + 1.1);
    z = min(z, F(1.5 * x - 2.2, 1.6 * y + 2.5));
    z = min(z, F(1.5 * x + 2.2, 1.6 * y + 2.5));
    z = min(z, F(0.92 * x, 0.85 * y - 0.7));
    z = min(z, F(1.3 * x + 1.5, 3.9 * y - 0.1));
    z = min(z, F(1.3 * x - 1.5, 3.9 * y - 0.1));
    z = min(z, F(1.55 * x - 1.5, 3.9 * y - 7.5));
    z = min(z, F(1.55 * x + 1.5, 3.9 * y - 7.5));
    float f = 1.25 / z; // 0.5*z; 

    // Time varying pixel color
    vec3 col = vec3(-f/2.0, 0.0, -f/5.0);

    // Output to screen
    fragColor = vec4(col, 1.0);
}
