void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = fragCoord.xy / iResolution.xy;

    vec2 gpos = pos - vec2(0.5, 0.5);

    vec3 col = vec3(0.1, 0.1, 0.1);

    if (iMouse.z > 0.0)
        col = vec3(1.0, 1.0, 1.0);

    col *= length(gpos);

    fragColor = vec4(col, 1.0);
}
