// remerciement a inigo quilez pour les fonctions de draw et la base du logo
// remerciement a Batman pour etre une source d'inspiration de tous les jours

vec2 rotate ( vec2 st, float a){
    st = mat2(cos(a), -sin(a), sin(a), cos(a))*(st-.5);
    return st+.5;
}

float Piconv(float n){
    n *=(3.14159265359/180.0);
    return n;
}

float ellipse( in vec2 p, in float x, in float y, in float dirx, in float diry, in float radx, in float rady )
{
	vec2  q = p - vec2(x,y);
	float u = dot( q, vec2(dirx,diry) );
	float v = dot( q, vec2(diry,dirx)*vec2(-1.0,1.0) );
	return dot(vec2(u*u,v*v),vec2(1.0/(radx*radx),1.0/(rady*rady)))-1.0;
}

float box( in vec2 p, in float x, in float y, in float dirx, in float diry, in float radx, in float rady )
{
	vec2  q = p - vec2(x,y);
	float u = dot( q, vec2(dirx,diry) );
	float v = dot( q, vec2(diry,dirx)*vec2(-1.0,1.0) );
	vec2  d = abs(vec2(u,v)) - vec2(radx,rady);
	return max(d.x,d.y);
}

float fillEllipse( in vec2 p, in float x, in float y, in float dirx, in float diry, in float radx, in float rady )
{
	float d = ellipse(p,x,y,dirx,diry,radx,rady);
    float w = fwidth(d);
	return 1.0 - smoothstep( -w, w, d);


}

float fillRectangle( in vec2 p, in float x, in float y, in float dirx, in float diry, in float radx, in float rady )
{
	float d = box(p,x,y,dirx,diry,radx,rady);
    float w = fwidth(d);
	return 1.0 - (smoothstep( -w, w, d));
}

// batmap logo
float logo( vec2 p )
{
	p *= 0.66;
	p.x = abs(p.x);

    vec2 q = p;	

	float f = 1.0;

	f = mix( f, 0.0,     fillEllipse(   q, 0.100, 0.00,  1.0, 0.0, 0.900, 0.444) );
	f = mix( f, 1.0,     fillEllipse(   q, 0.350, 0.300,  1.0, 0.0, 0.250, 0.167) );
	f = mix( f, 1.0,     fillRectangle( p, 0.180, 0.400,  1.0, 0.0, 0.070, 0.100) );
	f = mix( f, 1.0,     fillRectangle( p, 0.000, 0.400,  1.0, 0.0, 0.040, 0.040) );
	f = mix( f, 1.0,     fillRectangle( p, 0.036, 0.448,  0.6, 0.8, 0.065, 0.057) );
	f = mix( f, 1.0,     fillEllipse(   q, 0.200,-0.350,  1.0, 0.0, 0.300, 0.333) );
	f = mix( f, 1.0,     fillEllipse(   q, 0.500,-0.350, -0.8, 0.6, 0.250, 0.278) );
    //f = mix( f, 0.0, 1.0-fillEllipse(   p, 0.000, 0.000,  1.0, 0.0, 1.000, 0.556) );
	//f = mix( f, 0.0,     strokeEllipse( p, 0.000, 0.000,  1.0, 0.0, 0.950, 0.528, 0.06) );

	return f;
    //return dot(sign(f),f*0.5)/1.1;
}

float corps(vec2 p ) {
    p *= 0.66;
	p.x = abs(p.x);

    vec2 q = p;	

    float f = 1.0;
	//base corps
	f = mix( f, 0.0,     fillRectangle(   q, 0.100, -0.400,  1.0, 0.0, 0.700, 1.1) );

    //dessus epaule
	f = mix( f, 1.0,     fillRectangle(   q, 0.750, 0.350,  1., -0.25, 0.450, 0.567) );
    f = mix( f, 1.0,     fillRectangle( p, 0.300, 0.450,  1.0, -0.01, 0.150, 0.477) );

    //side epaule
    f = mix( f, 1.0,     fillRectangle(   q, 1.0, -0.60,  1., 0.25, 0.450, 0.867) );


    //oreille tete
	f = mix( f, 1.0,     fillRectangle( p, 0.130, 0.700,  1.0, 0.1, 0.050, 0.350) );
    f = mix( f, 1.0,     fillRectangle( p, 0.16, 0.17,  1.0, -0.05, 0.051, 0.205) );
	f = mix( f, 1.0,     fillRectangle( p, 0.026, 0.720,  0.6, 0.8, 0.165, 0.057) );

	f = mix( f, 1.0,     fillRectangle( p, 0., 0.600,  1.0, 0.0, 0.070, 0.10) );
    return f;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
