// 269

#define D(x,y) length( U - vec2(x,y)/3. ) < .5   // distance to disk(x,y)

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy,
          U = ( u+u - R ) / R.y;   
                //      / strip_size
    O.rgb   = int(u-.5) / 1 % 2 < 1              // vertical strips
               ? D(-.8,.5) ?                     // left disk
                              D(0,-1) ? vec3(0,0,1)  : vec3(0,1,1)  
               : D( 0, -1) ?                     // bottom disk
                              vec3(1,0,1) : vec3(1)
               : D( .8,.5) ?                     // right disk ( only one acting on odd strips )
                              vec3(0) : vec3(0,0,1);
}
