void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	float intensityPrev = iTime * 0.01;
	if (intensityPrev > 1.0)
	{
	intensityPrev = 1.0;
	}
    float intensity = sin(iTime * intensityPrev * 0.2) * 0.05 + intensityPrev;
	float vigintensity = 8.0 - 6.4 * intensity;
	vec2 vigcenter = vec2(0.5, 0.5);
    vec2 uv = fragCoord.xy / iResolution.xy;
    float x = uv.x * 6.0 + iTime;
    float y = uv.y * 6.0 + iTime;

    uv.x += cos(x + y) * intensity * 0.03 * cos(y);
    uv.y += sin(x + y) * intensity * 0.03 * sin(y);

	vec4 colorBase = texture(iChannel0, vec2(uv.x + sin(iTime * 1.2 + 1.575) * intensity * 0.02, uv.y + sin(iTime * 0.8 + 1.575) * intensity * 0.01));
	vec4 color1 = texture(iChannel0, vec2(uv.x + sin(iTime * 1.2) * intensity * 0.2, uv.y + sin(iTime * 0.8) * intensity * 0.1));
	vec4 color2 = texture(iChannel0, vec2(uv.x + sin(iTime * 1.2 + 3.15) * intensity * 0.2, uv.y + sin(iTime * 0.8 + 3.15) * intensity * 0.1));
	vec4 color3 = texture(iChannel0, vec2(uv.x + sin(iTime * 1.5) * intensity * 0.2, uv.y + sin(iTime * 1.1) * intensity * 0.1));
	vec4 color4 = texture(iChannel0, vec2(uv.x + sin(iTime * 1.5 + 3.15) * intensity * 0.2, uv.y + sin(iTime * 1.1 + 3.15) * intensity * 0.1));
	vec4 color5 = texture(iChannel0, vec2(uv.x + sin(iTime * 1.5 + 1.575) * intensity * 0.02, uv.y + sin(iTime * 1.1 + 1.575) * intensity * 0.01));
    vec4 staticeff = texture(iChannel1, iTime * 17.0 + uv);

	vec4 colorMix1 = mix(colorBase, color1, 0.5);
	vec4 colorMix2 = mix(colorMix1, color2, 1.0 / 3.0);
	vec4 colorMix3 = mix(colorMix2, color3, 0.25);
	vec4 colorMix4 = mix(colorMix3, color4, 1.0 / 5.0);
	vec4 colorMix5 = mix(colorMix4, color5, 1.0 / 6.0);

	float vigdistance  = distance(vec2(0.5, 0.5), uv)*1.414213;
	float vignette = clamp((vigintensity - vigdistance) / (vigintensity + intensity * 1.0), 0.0, 1.0);
	colorMix5 *= vignette;

	vec4 colorResult = mix(colorMix5, staticeff, 0.2 * intensity);

	fragColor = colorResult;
}
