//Bouncy - 140bytes version -> https://www.dwitter.net/d/24010

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;
    float x = uv.x*3.;
	float y = uv.y*3.;
	float r = x*x + y*y;
	float d = r > 1. ? sin(mod(iTime*2.3,3.2))/8.-.24:cos(r)/7.;
	float z = sin(x/d-iTime*9.)*cos(y/d-iTime*9.)*10000.;
    fragColor = vec4(z,z,z,1.0);
}
