void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float RADIUS = 10.0 * sin(iTime / 4.0);
    float RADIUS2 = 64.0;
    float RATE = 2.0 / RADIUS;

    vec2 origin = vec2(iResolution.x / 2.0, iResolution.y / 2.0) + (RADIUS2 * vec2(cos(RATE * iTime), sin(RATE * iTime)));

    vec2 dMouse = origin - fragCoord.xy;
    float brightness = 1.0 - (length(dMouse) / RADIUS);


    // Output to screen
    fragColor = vec4(sin(brightness), cos(brightness), sin(iTime + brightness), 1.0);
}
