void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 p = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
    p.x *= iResolution.x/iResolution.y;


    float a = 6050. -(iTime);;
    float b = 3. + 0.36*cos(
                    (p.x*p.x+2.5)/2.
                )-sin(
                    (p.y*p.y+2.5)/2.
                );

    //*vec3(0,1,1)
    //*vec3(1,0,1)
    float m1 = mod(a,b);
    float m2 = mod(b, 1.-a);



    fragColor = vec4(0.5*vec3(m1),1.0);
}
