#define freq(f) texture(iChannel0, vec2(f, 0.25)).x * 0.8


float avgFreq(float start, float end, float step) {
    float div = 0.0;
    float total = 0.0;
    for (float pos = start; pos < end; pos += step) {
        div += 1.0;
        total += freq(pos);
    }
    return total / div;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //make a color i guess? 
    vec3 icol = vec3(2,2,0);

    //bass, mids, treble levels for iChannel0
    float bassFreq = pow(avgFreq(0.0, 0.1, 0.01), 0.85);
    float medFreq = pow(avgFreq(0.1, 0.6, 0.01), 0.85);
    float topFreq = pow(avgFreq(0.6, 1.0, 0.01), 0.85);


    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(uv.xxy+icol);

    //variabls
    float xperiod = 5.;
    float amplitude = 5.;
    float stime = iTime*.1+20.;

    //magic matrix 
    mat2 tr = mat2(sin(uv.y*stime), sin(sin(stime+uv.x) + uv.y), 
               -sin(uv.x+stime), -cos(uv.x*stime)
    );

    //multply magic matrx
    uv *= tr;

    //draw the squiggles on the transformed plane 
    col += sin(bassFreq+uv.x*sin(uv.x*bassFreq*15.) + sin(stime)*cos(stime + uv.y*xperiod)) * .9;

    //add some color 
    col -= vec3(bassFreq*uv.x+.4, medFreq*uv.y+.4, topFreq*uv.x);

    // Output to screen
    fragColor = vec4(col,1.0);
}
