#define PI 3.141592653589793

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    vec2 cuv = uv;
    float ang = PI*.2;

    float pang = atan(cuv.y, cuv.x);    
    float curve = pang - iTime+.7*dot(uv,uv);
    float repeat = (mod(curve, ang))/ang;
    float soften = sin(repeat*PI);

    float x = soften;

    vec3 col = vec3(x,0.,uv.y+pow(uv.x*uv.y,3.));

//    col = vec3(.9*x);

    fragColor = vec4(col,1.0);
}
