void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 suv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    //Radial UVs    
    vec2 uv = vec2(length(suv),atan(suv.y,suv.x));

    //Stars
    float offset = .1*sin(uv.y*10.-iTime*.6)*cos(uv.y*48.+iTime*.3)*cos(uv.y*3.7+iTime);
    vec3 rays = vec3(sin(uv.y*150.+iTime)*.5+.5)* 
    vec3(sin(uv.y*80.-iTime*0.6)*.5+.5)*
    vec3(sin(uv.y*45.+iTime*0.8)*.5+.5)*
    vec3(1.-cos(uv.y+22.*iTime-pow(uv.x+offset,.3)*60.))*
    vec3(uv.x*2.);

    // Output to screen
    fragColor = vec4(rays.rg*.7,rays.b, 1.);
}
