void mainImage( out vec4 O, vec2 u )
{
    u /= iResolution.y;

    O = vec4(modf(u.y / .1, O.w) < .1 ? .5 : 1. / (fract(u.x / .2 + .15 * sin(O.w * 1.6)) - .5));
}
