precision highp float;

const float e = 2.71;
const float PI = 3.14;
const float R = e/PI;

vec2 rotZ(vec2 point, float angle) {
    float c = cos(angle);
    float s = sin(angle);
    vec2 finalPoint = mat2(c, -s, s, c) * point;
    return finalPoint;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float f = texture(iChannel0, vec2 (0.)).x;
    vec2 uv = ( 2. * fragCoord - iResolution.xy ) / iResolution.y * (1. + 0.1 * f);
    float r = exp((-(abs(length(uv) - R)) + 0.01 * f)/(0.1 * f));
    vec3 color = vec3(r);
    fragColor = vec4(color, 1.);
}
