#ifdef GL_ES
precision mediump float;
#endif

#define PI 3.14159265359

float stroke(float p, float w , float v)
{
    return smoothstep(1.5/iResolution.y, 0., abs(v-p) - w);
}

float fill(float p, float s)
{
    return smoothstep(p, s, 1.);
}

mat2 rotate2d(float angle)
{
    return mat2(cos(angle), -sin(angle),
                sin(angle), cos(angle));
}

float circleSDF(vec2 st, vec2 center)
{
    return length(st - center);
}

float radarCross(vec2 st, vec2 center, float width, float rotation)
{
    mat2 r = rotate2d(radians(rotation));
    st =  (st - vec2(.5)) * r + vec2(.5);
    center =  (center - vec2(.5)) * r + vec2(.5);
    return stroke(center.x, width, st.x) + stroke(center.y, width, st.y);
}

float pulse(vec2 st, vec2 center, float freq, float maxAmpl, float width)
{
    float c = circleSDF(st, center);
    return stroke(maxAmpl * fract(iTime * freq), width, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = (fragCoord - .5*iResolution.xy)/iResolution.y ;
    vec3 color = vec3(0.);

    vec2 center = vec2(.0,.0);

    float c = circleSDF(st, center);
    color += fill(c, .008);
    for(float i = 0.; i < 4.; i++)
    {
        color += stroke(.12 * i, .0, c);
    }
    color += stroke(.48, .002, c);

    color += radarCross(st, center, .0, 45.) * step(c, 0.481);
    color += 0.5 * pulse(st, center, .65, .48, .0004);

    st += vec2(cos(.75*iTime), sin(.65*iTime)) * 0.35;
    float c2 = circleSDF(st, center);
    color += vec3(1.,0. , 0.) * (fill(c2, 0.008) + pulse(st,center, .7,.05,.0004)) * step(c,0.48);

    // Output to screen
    fragColor = vec4(color,1.0);
}
