void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    uv -= 0.5;
    uv*= 6.824;
    uv.y*=.5625;
    float col = atan(uv.x,uv.y);
    float x = uv.x;
    float y = uv.y;
    float k = 1.;
    col += sin(x*x+y*y-iTime);
    vec3 col3 = vec3(sin((col+iTime/10.)*40.),
    				sin((col+iTime/11.)*40.),
    				sin((col+iTime/12.)*40.));

    // Output to screen
    fragColor = vec4(col3,1.0);
}
