#define NR_ITER 30.
vec2 Mul(vec2 i1,vec2 i2)
{
    return vec2(i1.x*i2.x-i1.y*i2.y,i1.x*i2.y+i1.y*i2.x);
}

float MandelBrot(vec2 p)
{
    float val = 0.;
    int i = int(NR_ITER);

    vec2 nz = vec2(0,0);
    while(i--!=0)
    {
        nz = Mul(nz,nz) + p;
    }
    return length(nz);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = float(iMouse.z>0.);
    vec2 normMouse = (vec2(iMouse) * 2. - vec2(iResolution))/iResolution.y;
    vec2 uv = (fragCoord * 2. - vec2(iResolution))/iResolution.y;

    float mandel = MandelBrot(uv*mix(1.,.1,zoom) + normMouse);
    vec3 col = mix(vec3(0,0,1),vec3(1,.5,0),mandel);
    fragColor = vec4(col,1.0);
}
